import { Button } from 'bootstrap';
import React, { Component } from 'react';

export default class Counter extends Component {
    state = {
        id:this.props.id,
        value:this.props.va
        
    }

    styles={
        fontSize:15,
        fontWeight:'bold'
    }
    render() { 
    //React.createElement('div')
    
    return(<div>
        
        <span style={this.styles} className={this.getBadge()}>{this.formatCount()}</span>
        <button onClick={this.handleIncrement} className="btn btn-secondary btn-sm m-2">Increment</button>
        <button onClick={this.handleDecrement} className="btn btn-secondary btn-sm ">Decrement</button>
        <button onClick={() => this.props.onDelete(this.props.id)} className="btn btn-danger btn-sm  m-2">Delete</button>
        
       </div>);

    }
    handleIncrement =()=>{
    this.setState({value: this.state.value+1})
     
    }

    handleDecrement =()=>{return this.state.value ===0 ? this.state.value : this.setState({value: this.state.value-1});
    
         
        }
    getBadge() {
        let classes="badge m-2 badge-";
        classes += this.state.value === 0 ? "warning" : "primary";
        return classes;
    }

    formatCount(){
        const{value}=this.state;
        return value ===0 ?"Zero":value;
    }
}
