import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import Counter from './counter';

export default class Counters extends Component {
    state = {
        counters:[
            {id:1, value:0},
            {id:2, value:1},
            {id:3, value:2},
            {id:4, value:4}]
    };


    handleDelete= counterId =>{
        
       const counters=this.state.counters.filter(c=>c.value!== counterId);
        this.setState({counters});
    };
    handleDelete=()=>{
        const counters=this.state.counters.map(c =>{
            c.value=0;
            return c;
        });
        this.setState()
    }


    render() { 
        return (<div>
            <button className="btn btn-primary btn-sm m-2" onClick={this.handleReset}>Reset</button>

            {this.state.counters.map(counter => <Counter key={counter.id} onDelete={this.handleDelete} value={counter.value}/>)}

            
        </div>  );
    }
  
}
