import { Button } from 'bootstrap';
import React, { Component } from 'react';

export default class Counter extends Component {
    state = {
        count:0,
        
    }

    styles={
        fontSize:15,
        fontWeight:'bold'
    }
    render() { 
    //React.createElement('div')
    
    return(<div>
        
        <span style={this.styles} className={this.getBadge()}>{this.formatCount()}</span>
        <button onClick={this.handleIncrement} className="btn btn-secondary btn-sm m-2">Increment</button>
        <button onClick={this.handleDecrement} className="btn btn-secondary btn-sm ">Decrement</button>
        
       </div>);

    }
    handleIncrement =()=>{
    this.setState({count: this.state.count+1})
     
    }

    handleDecrement =()=>{
        const {c}=this.state;
        c ===0 ?this.setState({count: this.state.count=0}):this.setState({count: this.state.count-1})
         
        }
    getBadge() {
        let classes="badge m-2 badge-";
        classes += this.state.count === 0 ? "warning" : "primary";
        return classes;
    }

    formatCount(){
        const{count}=this.state;
        return count ===0 ?"Zero":count;
    }
}
